(function ($) {

	$.fn.stickyInfiniteSidebar = function (options) {
		var config = $.extend({
			wrapperSelector: '#content',
			sidebarTopMargin: 90
		}, options);

		var fixSidebr = function () {

			var viewportHeight = $(window).outerHeight();
			var viewportWidth = $(window).outerWidth();
			var documentHeight = $(document).height();
			var content = $(this);
			var contentTop = content.offset().top;
			var contentHeight = $(content).outerHeight();
			var contentBottom = contentTop + contentHeight;
			var wrapper = $(content).closest(config.wrapperSelector);
			var wrapperTop = $(wrapper).offset().top;
			var wrapperHeight = $(wrapper).outerHeight(true);
			var breakingPoint1 = ($(wrapper).offset().top - ($('#primary-bar').outerHeight(true) + $('#wpadminbar').outerHeight(true) +$('#secondary-bar').outerHeight(true)));
